beforeSaveCustomScript:
// 1. Calculate Open vs Closed Cases
// based on your Status options: Open, In Progress, Resolved, Close, Escalated
if (status == 'Close' || status == 'Resolved') {
    isClosedInt = 1;
    isOpenInt = 0;
} else {
    // Covers Open, In Progress, Escalated
    isClosedInt = 0;
    isOpenInt = 1;
}

// 2. Calculate SLA Breach
// based on your SLA Status field name 'cSLAStatus'
if (cSLAStatus == 'Breached') {
    isBreachedInt = 1;
} else {
    isBreachedInt = 0;
}
