beforeSaveCustomScript:
// Check if the custom contact link is present
if (cContactId != null) {
    // Copy fields from the linked Contact using "cContact"
    firstName = cContact.firstName;
    lastName = cContact.lastName;
    emailAddress = cContact.emailAddress;
    phoneNumber = cContact.phoneNumber;

    // Address Fields
    addressStreet = cContact.addressStreet;
    addressCity = cContact.addressCity;
    addressState = cContact.addressState;
    addressCountry = cContact.addressCountry;
    addressPostalCode = cContact.addressPostalCode;

    // Auto-generate the main Name field so the record isn't blank in the list
    name = string\concatenate(firstName, ' ', lastName);
}
